$(document).ready(function(){var a=$(".layer").width()*1.777;$(".layer").css("height",Math.ceil(a));$(".phoneLayers").addClass("popOut");setTimeout(function(){$(".phoneLayers").addClass("noDelay")},300)});$(".layer").click(function(){if(!$(this).hasClass("active")){$(this).addClass("active");$(this).siblings().removeClass("active")}else{$(this).removeClass("active")}});$(window).resize(function(){var a=$(".layer").width()*1.777;$(".layer").css("height",a)});